import { Marquee } from "@/components/nurui/marque";
import TestimonialCard from "@/components/nurui/testimonial-card";
import { testimonialData } from "@/data/testimonial";

export default function MarqueeTestimonialDemo() {
  return (
    <section
      className="m-6 relative flex flex-col lg:flex-row gap-6 overflow-hidden
    border-y md:border border-[var(--primary-color)] md:rounded-2xl md:px-5 xl:px-6 2xl:px-10 md:mx-6 "
    >
      {/* title */}
      <div className="w-[40%] space-y-3.5 hidden 2xl:flex flex-col justify-center items-start">
        <p className="py-1 uppercase px-2 bg-[var(--primary-color-4)] inline-block text-[var(--primary-color)] font-semibold rounded-lg">
          Community
        </p>
        <h1 className="text-7xl font-black pb-2">
          Hear From Our Happy{" "}
          <span className="text-[var(--primary-color)]">People</span>
        </h1>
        <p className="text-balance text-lg">
          Here&apos;s what some of our users have to say about nurui UI.
          Designing became faster, smoother, and way more enjoyable. From
          startups to scaleups, developers trust our components daily.
        </p>
      </div>

      {/* testimonial cards */}
      <div className="flex flex-row items-center md:gap-2.5 xl:gap-8 2xl:w-[60%] mx-auto py-4 md:py-0 max-h-[calc(100vh-6rem)]">
        {[
          { data: testimonialData?.slice(0, 10), reverse: true },
          { data: testimonialData?.slice(10, 20), reverse: false },
          // { data: data?.slice(20, data?.length), reverse: true },
        ].map((item, idx) => (
          <Marquee
            key={idx}
            pauseOnHover
            vertical
            reverse={item.reverse}
            className="[--duration:100s] hidden md:block"
          >
            {item.data?.map((tes, i) => (
              <TestimonialCard
                src={tes?.image}
                key={i}
                position={tes?.position}
                name={tes?.name}
                review={tes?.testimonial}
                marginTop={i + 1 === 1 ? "mt-4" : ""}
              />
            ))}
          </Marquee>
        ))}
        {[
          { data: testimonialData?.slice(0, 10), reverse: true },
          // { data: data?.slice(10, 20), reverse: false },
        ].map((item, idx) => (
          <Marquee
            key={idx}
            pauseOnHover
            vertical
            reverse={item.reverse}
            className="[--duration:100s] block md:hidden"
          >
            {item.data?.map((tes, i) => (
              <TestimonialCard
                src={tes?.image}
                key={i}
                position={tes?.position}
                name={tes?.name}
                review={tes?.testimonial}
                marginTop={i + 1 === 1 ? "mt-4" : ""}
              />
            ))}
          </Marquee>
        ))}
      </div>
    </section>
  );
}

import { cn } from "@/lib/utils";
import { ComponentPropsWithoutRef } from "react";

interface MarqueeProps extends ComponentPropsWithoutRef<"div"> {
  /**
   * Optional CSS class name to apply custom styles
   */
  className?: string;
  /**
   * Whether to reverse the animation direction
   * @default false
   */
  reverse?: boolean;
  /**
   * Whether to pause the animation on hover
   * @default false
   */
  pauseOnHover?: boolean;
  /**
   * Content to be displayed in the marquee
   */
  children: React.ReactNode;
  /**
   * Whether to animate vertically instead of horizontally
   * @default false
   */
  vertical?: boolean;
  /**
   * Number of times to repeat the content
   * @default 4
   */
  repeat?: number;
  itemsCenter?: string;
}

export function Marquee({
  className,
  reverse = false,
  pauseOnHover = false,
  children,
  vertical = false,
  repeat = 4,
  itemsCenter = "",
  ...props
}: MarqueeProps) {
  return (
    <div
      {...props}
      className={cn(
        "group flex overflow-hidden p-2 [--duration:40s] [--gap:1rem] [gap:var(--gap)]",
        {
          "flex-row": !vertical,
          "flex-col": vertical,
        },
        className,
      )}
    >
      {Array(repeat)
        .fill(0)
        .map((_, i) => (
          <div
            key={i}
            className={cn(
              "flex shrink-0 justify-around [gap:var(--gap)]",
              itemsCenter,
              {
                "animate-marquee flex-row": !vertical,
                "animate-marquee-vertical flex-col": vertical,
                "group-hover:[animation-play-state:paused]": pauseOnHover,
                "[animation-direction:reverse]": reverse,
              },
            )}
          >
            {children}
          </div>
        ))}
    </div>
  );
}

import { FaQuoteLeft } from "react-icons/fa6";
import Image from "next/image";
import RatingStars from "@/components/nurui/rating-star";

interface IProps {
  position: string;
  name: string;
  review: string;
  marginTop?: string;
  src: string;
}

export default function TestimonialCard({
  position,
  name,
  review,
  marginTop,
  src,
}: IProps) {
  return (
    <div
      className={`cursor-pointer md::w-52 lg:w-full p-4 shadow-2xl rounded-lg bg-[var(--glass-color)] dark:bg-[var(--glass-color-2)] border border-[var(--border-color)] relative ${marginTop}`}
    >
      <FaQuoteLeft className=" absolute -top-2 left-[5%] text-[1.3rem] text-[#727272]" />
      <p className="text-text text-[0.9rem] text-center lg:text-left">
        {review}
      </p>

      <div className="flex flex-col lg:flex-row items-center lg:items-start mt-5 justify-between gap-3 lg:gap-0">
        <div className="flex flex-col lg:flex-row items-center gap-2.5">
          <Image
            src={src}
            alt=""
            className="size-8 rounded-full"
            height={0}
            width={0}
            unoptimized
          />
          <div className="text-center lg:text-start">
            <h2 className="text- font-[600]">{name}</h2>
            <p className="text-sm text-[#727272]">{position}</p>
          </div>
        </div>

        <RatingStars size="size-3" />
      </div>
      <FaQuoteLeft className=" absolute -bottom-2 right-[5%] rotate-[180deg] text-[1.3rem] text-[#727272]" />
    </div>
  );
}

import RatingIcon from "@/components/nurui/rating-icon";

const RatingStars = ({
  count = 5,
  size,
}: {
  size?: string;
  count?: number;
}) => (
  <div className="flex items-center">
    {Array.from({ length: count }).map((_, i) => (
      <RatingIcon key={i} rate={i < 4} size={size} />
    ))}
  </div>
);

export default RatingStars;
