"use client";
import React from 'react';
import styled from 'styled-components';

const QuadRingLoader = () => {
  return (
    <StyledWrapper>
      <svg className="pl" viewBox="0 0 128 128" width="128px" height="128px">
        <g fill="none" strokeWidth="8" strokeLinecap="round">
          <circle className="pl__ring pl__ring--a" r="56" cx="64" cy="64" />
          <circle className="pl__ring pl__ring--b" r="56" cx="64" cy="64" />
          <circle className="pl__ring pl__ring--c" r="56" cx="64" cy="64" />
          <circle className="pl__ring pl__ring--d" r="56" cx="64" cy="64" />
        </g>
      </svg>
    </StyledWrapper>
  );
};

const StyledWrapper = styled.div`
  .pl {
    display: block;
    margin: auto;
  }
  .pl__ring {
    animation: ringA 2s linear infinite;
  }
  .pl__ring--a { stroke: #ff0080; animation-name: ringA; }
  .pl__ring--b { stroke: #7928ca; animation-name: ringB; }
  .pl__ring--c { stroke: #00f5ff; animation-name: ringC; }
  .pl__ring--d { stroke: #00ff85; animation-name: ringD; }

  @keyframes ringA {
    0% { stroke-dasharray: 0 352; stroke-dashoffset: 0; }
    50% { stroke-dasharray: 176 176; stroke-dashoffset: -88; }
    100% { stroke-dasharray: 0 352; stroke-dashoffset: -352; }
  }

  @keyframes ringB {
    0% { stroke-dasharray: 0 352; stroke-dashoffset: 0; }
    50% { stroke-dasharray: 176 176; stroke-dashoffset: -176; }
    100% { stroke-dasharray: 0 352; stroke-dashoffset: -352; }
  }

  @keyframes ringC {
    0% { stroke-dasharray: 0 352; stroke-dashoffset: 0; }
    50% { stroke-dasharray: 176 176; stroke-dashoffset: -264; }
    100% { stroke-dasharray: 0 352; stroke-dashoffset: -352; }
  }

  @keyframes ringD {
    0% { stroke-dasharray: 0 352; stroke-dashoffset: 0; }
    50% { stroke-dasharray: 176 176; stroke-dashoffset: -352; }
    100% { stroke-dasharray: 0 352; stroke-dashoffset: -352; }
  }
`;

export default QuadRingLoader;
