import RippleLoader from "@/components/nurui/ripple-loader";

const RippleLoaderDemo = () => {
  return (
    <>
      <p className="text-3xl text-center pt-4 text-[#00FF87] font-bold">
        Watch the ripple animation.
      </p>
      <RippleLoader />
    </>
  );
};

export default RippleLoaderDemo;

"use client";
import React from "react";

const RippleLoader = () => {
  const colors = [
    "#cce4fc",
    "#99c9fa",
    "#66afe0",
    "#3ca2fa",
    "#1f8de6",
    "#1073d1",
    "#095abf",
    "#0347aa",
    "#012f82"
  ];

  const delays = [0, 100, 200, 100, 200, 200, 300, 300, 400];

  return (
    <>
      <style jsx>{`\n        @keyframes ripple {\n          0% { background-color: transparent; }\n          30% { background-color: var(--cell-color); }\n          60% { background-color: transparent; }\n          100% { background-color: transparent; }\n        }\n      `}</style>

      <div className="flex flex-wrap w-[162px] h-[162px]">
        {colors.map((color, index) => (
          <div
            key={index}
            style={{
              "--cell-color": color,
              animationDelay: `${delays[index]}ms`,
              animation: "ripple 1.5s ease infinite"
            } as React.CSSProperties}
            className="flex-none w-[52px] h-[52px] m-[1px] rounded-md"
          />
        ))}
      </div>
    </>
  );
};

export default RippleLoader;
