import RippleLoader from "@/components/nurui/ripple-loader";

const RippleLoaderDemo = () => {
  return (
    <>
      <p className="text-3xl text-center pt-4 text-[#00FF87] font-bold">
        Watch the ripple animation.
      </p>
      <RippleLoader />
    </>
  );
};

export default RippleLoaderDemo;

"use client";
import React from "react";

interface RippleLoaderProps {
  /** Custom colors for the ripple cells */
  colors?: string[];
  /** Size of each cell (px) */
  cellSize?: number;
  /** Grid size (default 3 â†’ 3x3) */
  cells?: number;
  /** Delay between animations in ms */
  delayStep?: number;
}

const RippleLoader: React.FC<RippleLoaderProps> = ({
  colors = [
    "#00FF87",
    "#0CFD95",
    "#17FBA2",
    "#23F9B2",
    "#30F7C3",
    "#3DF5D4",
    "#45F4DE",
    "#53F1F0",
    "#60EFFF"
  ],
  cellSize = 52,
  cells = 3,
  delayStep = 100
}) => {
  const totalSize = cells * (cellSize + 2);

  return (
    <>
      <style jsx>{`
        @keyframes ripple {
          0% { background-color: transparent; }
          30% { background-color: var(--cell-color); }
          60% { background-color: transparent; }
          100% { background-color: transparent; }
        }
      `}</style>

      <div className="flex flex-wrap" style={{ width: totalSize, height: totalSize }}>
        {Array.from({ length: cells * cells }).map((_, index) => (
          <div
            key={index}
            style={{
              "--cell-color": colors[index % colors.length],
              animationDelay: `${(index % cells) * delayStep}ms`,
              animation: "ripple 1.5s ease infinite",
              width: cellSize,
              height: cellSize
            } as React.CSSProperties}
            className="flex-none m-[1px] rounded-md"
          />
        ))}
      </div>
    </>
  );
};

export default RippleLoader;
