import React from "react";
import { FaRocket } from "react-icons/fa6";
import ShinyCard from "@/components/nurui/shiny-card";

export default function ShinyCardDemo() {
  return (
    <div className="p-10">
      {data.map((feature, index) => (
        <ShinyCard
          key={index}
          featureName={feature.featureName}
          featureItems={feature.featureItems}
          icon={feature.icon}
        />
      ))}
    </div>
  );
}

const data = [
  {
    featureName: "Fast Performance",
    featureItems: [
      "Optimized for speed",
      "Low latency",
      "Lightweight and efficient",
    ],
    icon: <FaRocket />,
  },
];

import { ReactNode } from "react";
import { MdDone } from "react-icons/md";
import "./styles/shiny-card.css";

interface ShinyCardProps {
  featureName: string;
  featureItems: string[];
  icon: ReactNode | string;
}

const ShinyCard: React.FC<ShinyCardProps> = ({
  featureName,
  featureItems,
  icon,
}) => {
  return (
    <div className="shiny-card space-y-3 min-h-72 shadow-2xl dark:shadow-none">
      <p className="text-5xl text-[var(--primary-color)] mb-5 mt-1">{icon}</p>
      <h2 className="text-xl font-bold">{featureName}</h2>
      <ul className="space-y-2">
        {featureItems.map((item, index) => (
          <li
            key={index}
            className="text-pretty font-semibold flex items-center gap-2.5"
          >
            <span className="size-4 flex items-center justify-center p-0.5 bg-[var(--primary-color)] text-[var(--black-color)] rounded-full">
              <MdDone />
            </span>
            <span>{item} </span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ShinyCard;

.shiny-card {
  --white: hsl(0, 0%, 100%);
  --black: hsl(240, 15%, 9%);
  --paragraph: hsl(0, 0%, 83%);
  --line: hsl(240, 9%, 17%);
  --primary: var(--primary-color);
  gap: 1rem;
  padding: 1rem;
  background-color: hsla(240, 15%, 9%, 1);
  background-image:
    radial-gradient(at 88% 40%, hsla(240, 15%, 9%, 1) 0px, transparent 85%),
    radial-gradient(at 49% 30%, hsla(240, 15%, 9%, 1) 0px, transparent 85%),
    radial-gradient(at 14% 26%, hsla(240, 15%, 9%, 1) 0px, transparent 85%),
    radial-gradient(at 0% 64%, hsl(189, 99%, 26%) 0px, transparent 85%),
    radial-gradient(at 41% 94%, hsl(189, 97%, 36%) 0px, transparent 85%),
    radial-gradient(at 100% 99%, hsl(188, 94%, 13%) 0px, transparent 85%);

  border-radius: 1rem;
  box-shadow: 0px -16px 24px 0px rgba(255, 255, 255, 0.25) inset;

  border: 2px solid var(--primary);
  animation: border-color-change 3s linear infinite;
}

@keyframes border-color-change {
  0% {
    border-color: var(--primary);
  }

  50% {
    border-color: var(--line);
  }

  100% {
    border-color: var(--primary);
  }
}
